import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import { pdfjs } from 'react-pdf';
import {toast} from "react-toastify";
import { Document, Page } from 'react-pdf';
import pdfFile from "./m.pdf";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

function App() {
  // getContext('2d', { willReadFrequently: true });

  const [numPages, setNumPages] = useState(1);
  const [pageNumber, setPageNumber] = useState(1);

  const onDocumentLoadSuccess = (numPages) => {
    setNumPages(numPages);
  }

  return (
    <div>
      {/* <div>sdfsdfsgdfsdsf</div> */}
      {/* <iframe src="https://firebasestorage.googleapis.com/v0/b/flutter3test-478a4.appspot.com/o/m.pdf?alt=media&token=519c9147-4bdb-4428-8490-0e16df8c87cd"/> width=100%*/}
      <Document url={"https://firebasestorage.googleapis.com/v0/b/flutter3test-478a4.appspot.com/o/m.pdf?alt=media&token=519c9147-4bdb-4428-8490-0e16df8c87cd"} 
        onLoadSuccess={onDocumentLoadSuccess} 
        onSourceError={(error) => toast.error('Error while retrieving document source! ' + error.message)}>
        <Page
            size="A4"
            scale={500 * 0.3}
            pageNumber={1}
            height= {500}
            renderAnnotationLayer={false}
            renderTextLayer={false}
          >
        </Page>
      </Document>
    </div>
  );
}

export default App;
